prompt --application/shared_components/logic/application_processes/set_notification_globals
begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(49216835370353580)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SET_NOTIFICATION_GLOBALS'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  v_notification_count  number := 0;',
'begin',
'  apex_json.open_object;',
'',
'  begin',
'    select trim (to_char (count (*), ''999G999G999G999G990''))',
'      into :a_notifications',
'      from wksp_evp.evp_vehicle_notification ven',
'           left join wksp_evp.evp_notification_user_act nua',
'                on nua.notification_id = ven.id',
'     where (nvl (nua.activity_type, ''X'') not in (''DISMISSED'', ''OPENED''))',
'           and (ven.receiver = :app_user);',
'',
'    v_notification_count := :a_notifications;',
'    :P0_NOTIFICATION_COUNT := :a_notifications;',
'',
'    apex_json.write (''success'', true); ',
'    ',
'  exception',
'    when others',
'    then',
'      apex_json.write (''error'', true);',
'      apex_debug.error (',
'        ''Exception kod popunjavanja indikatora notifikacija    err_msg:%s''',
'       ,sqlerrm);',
'  end;',
'',
'    apex_json.write (''notificationCount'', v_notification_count); ',
'    apex_json.close_object;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_version_scn=>41370693334627
);
